var _ = require('underscore');

var AuthzConstants = require('oae-authz/lib/constants').AuthzConstants;
var log = require('oae-logger').logger('oae-jitsi-rest');
var OAE = require('oae-util/lib/oae');
var OaeUtil = require('oae-util/lib/util');

var FavoritesAPI = require('oae-favorites');

/**
 * @REST getFavorites
 *
 * Get all the favorites from a principal
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /favorites/library/{principalId}
 * @PathParam   {string}                principalId         The id of the principal 
 * @Return      {favorites}                                 The favorites list
 * @HttpResponse                        200                 Meeting library available
 * @HttpResponse                        400                 A user or group id must be provided
 * @HttpResponse                        401                 You do not have have access to this library
 */
OAE.tenantRouter.on('get', '/api/favorites/library/:principalId', function(req, res) {

    FavoritesAPI.getFavorites(req.ctx, req.params.principalId, function(err, favorites) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, {'results': favorites});
    });
});

/**
 * @REST addToFavorites 
 *
 * Add Favorite
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /favorites/addToFavorites/
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            idPrincipal         The id of the principal          
 * @FormParam   {string}            idResources         The id of the resource             
 * @Return      {resource}                              The favorite resource added             
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/favorites/addToFavorites/', function (req, res) {

    FavoritesAPI.addToFavorites(req.ctx, req.body.idPrincipal, req.body.idResources, function (err, resources) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, resources);
    });
});

/**
 * @REST removeFavorite 
 *
 * Remove Favorite
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /transfer/favorites/removeFavorite
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            idPrincipal         The id of the principal          
 * @FormParam   {string}            idResources         The id of the resource             
 * @Return      {resource}                              The favorite resource remouved
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/favorites/removeFavorite/', function (req, res) {

    FavoritesAPI.removeFavorite(req.ctx, req.body.idPrincipal, req.body.idResources, function (err, resource) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        return res.send(200, resource);
    });
});
